import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import dogFoodImage from '../images/dogfood.jpg';
import catToyImage from '../images/cattoy.png';

function Products() {
  const productList = [
    { id: 1, name: 'Dog Food', price: '$20', description: 'High quality dog food.', image: dogFoodImage },
    { id: 2, name: 'Cat Toy', price: '$10', description: 'Fun toy for your cat.', image: catToyImage },
  ];

  return (
    <Container>
      <h1>Products</h1>
      <Row>
        {productList.map(product => (
          <Col key={product.id} sm={12} md={6} lg={4} className="mb-4">
            <Card>
              <Card.Img variant="top" src={product.image} className="product-image" />
              <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Text>{product.description}</Card.Text>
                <Card.Text><strong>{product.price}</strong></Card.Text>
                <Button variant="primary">Buy Now</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Products;
